"use strict";

var DI = require('../DescriptiveIncident');

var di = new DI({
  a: 'a',
  b: 'b'
}, {
  duration: 1,
  c: 'c',
  d: 'd'
});
test('editing attributes on an unassigned DI should pass', function () {
  var res = di.editAttributes({
    a: 'a1',
    b: 'b1'
  });
  expect(res).toEqual({
    result: true
  });
  expect(di.attrs).toEqual({
    a: 'a1',
    b: 'b1'
  });
});
test('editing properties on an unassigned DI should pass', function () {
  var res = di.editProperties({
    c: 'c1',
    d: 'd1'
  });
  expect(res).toEqual({
    result: true
  });
  expect(di.props).toEqual({
    c: 'c1',
    d: 'd1'
  });
});