const Helper = require('../../_coreUtils/Helper');
const helper = new Helper();
const _TREECONST = require('../../Tree/_CONST');

function unblock(pld) {
    pld.descriptor.value = function (description) {
        this.DescriptiveIncident.putMessageOnPipe('unBlock', {
                id: this.blockID
            },
            'rootClip', { selfExecute: true, direction: _TREECONST._DIRECTIONS._UP }
        );
    }
}

module.exports = unblock;
