const DescriptiveClip = require('./DescriptiveClip');

class DescriptiveAudioClip extends DescriptiveClip {
    constructor(audioSources, id = null) {
        let propsToPass = {
            audio: 'only',
            audioSources: audioSources
        };
        if (id !== null) {
            propsToPass.id = id;
        }
        super(propsToPass);
    }

}

module.exports = DescriptiveAudioClip;
