import DescriptiveClip from './DescriptiveClip';

class DescriptiveAudioClip extends DescriptiveClip {
  constructor(audioSources, id = null) {
    const propsToPass = {
      audio: 'only',
      audioSources,
    };
    if (id !== null) {
      propsToPass.id = id;
    }
    super(propsToPass);
  }
}

export default DescriptiveAudioClip;
