const CAI = require('./ContextAwareIncident');

class ContextAwareKeyframedIncident extends CAI {
    /** METHODS THAT ACT AS "PROXY" TO ALL OF THE INCIDENTS BELONGING TO
     * THE INCIDENTS COLLECTION OF THE CONTEXT AWARE INCIDENT
     **/
    get animatedAttributeValue() {
        return this.constructionIngredients.attrs.animatedAttrs[this.attribute][this.constructionIngredients.attrs.animatedAttrs[this.attribute].length - 1].value;
    }

}

module.exports = ContextAwareKeyframedIncident;
