/**
 * this method acts as a proxy to duration setter by taking a fraction and not
 * an absolute number for the overall duration of the Leaf
 * */
function systoleDiastole(payload) {
  payload.descriptor.value = function systoleDiastoleFunct(durationFraction) {
    this.duration = this.duration * durationFraction;
  };
}

export default systoleDiastole;
