function getIncidentsByChannel(pld) {
    pld.descriptor.value = function (adjustMillisecond = 0, parentMillisecond = 0) {
        let incidentsByPlugin = {};
        incidentsByPlugin[this.mc_plugin_npm_name] = [{
            millisecond: adjustMillisecond,
            parentMillisecond: parentMillisecond,
            incident: this,
            id: this.id
        }];
        return incidentsByPlugin;
    }
}

module.exports = getIncidentsByChannel;
